import React from 'react';
import axios from "axios";
import CadastroUsuario from './components/CadastroUsuario';
import ListaUsuarios from './components/ListaUsuarios';

export default class App extends React.Component {
  state = {
    listaCadastrados: [],
    erro: "",
    nomeCadastro: "",
     emailCadastro:""
  };

 cadastrarUsuario = () => {
  const novoUsuario = {
    name: this.state.nomeUsuario
  };
  axios
  .post ("https://us-central1-labenu-apis.cloudfunctions.net/labenusers/users", novoUsuario,
  {
    headers: {
      Authorization: "lais-martins-alves"
    }

  }).then((cadastrado) => {
  console.log(cadastrado);
  alert("seu cadastro foi enviada"); 
}).catch((erro) => {
  alert(erro.response.data.message);
})
}

novoNomeUsuario = (event) => {
  this.setState({ nomeCadastro: event.target.value });
};

  render() {
    return (
      <>
        <CadastroUsuario/>
        <ListaUsuarios/>
      </>
    )
  }
}


