import React from 'react';
import axios from "axios";
import styled from "styled-components";

const CardLista = styled.div `
background-color: beige;
`


export default class ListaUsuarios extends React.Component {
  state = {
    usuarios: []
  }

  componentDidMount() {
    this.pegandoUsuarios()
  }

  pegandoUsuarios = () => {
    const url = "https://us-central1-labenu-apis.cloudfunctions.net/labenusers/user"
    axios.get(url, {
      headers: {
        Authorization: "lais-martins-alves"
      }
    })
    .then((resposta) => {
      this.setState({usuarios: resposta.data})
    })
      .catch((erro) => {
        alert("Ocorreu um erro! Tente novamente.")
      })
  }
  render() {
    const listaDeUsuarios = this.state.usuarios.map((pessoa) => {
      return <CardLista>{pessoa.name}</CardLista>
    }) 

    return (
      <div>ListaUsuarios
        <br />
        <hr />
        <button onClick={this.props.vaiParaCadastro}>Ir Para Cadastro de Usuários</button>
        <hr/>
        <br/>
        <h2>Lista de Usuários</h2>
        {listaDeUsuarios}
      </div>

    )
  }
}
