import React from 'react';
import axios from "axios";
import styled from 'styled-components';

const ContainerPlaylist = styled.div`
border: 1px solid black;
width: 500px;
padding: 5px;
margin: 10px;
`


export default class TelaPlaylists extends React.Component {
    
    state = {
        listas: []
    }

    componentDidMount() {
        this.pegaListas()
    }

    pegaListas = () => {
        const url = "https://us-central1-labenu-apis.cloudfunctions.net/labefy/playlists"
        axios.get( url, {
            headers: {
                Authorization: "lais-martins-alves"
            }
        })
        .then((res) => {
            this.setState({listas: res.data})
        })
        .catch((erro) => {
            alert("Erro! Tente novamente.")
        })
    }   
  render() {
     const cadaPlaylist = this.state.listas.map((list) => {
        return <ContainerPlaylist>{list.name}</ContainerPlaylist>
    })

    return (
      <>
      <button onClick={this.props.irCriar}>Criar Nova Playlist</button>
      <hr/>
      {cadaPlaylist}
      </>
    )
  }
}
