import React from 'react';
import axios from "axios";
import styled from 'styled-components';

const ContainerPlaylist = styled.div`
border: 1px solid black;
width: 500px;
padding: 5px;
margin: 10px;
`


export default class TelaPlaylists extends React.Component {
    
    state = {
        listas: []
    }

    componentDidMount() {
        this.pegaListas()
    }

    pegaListas = () => {
        const url = "https://us-central1-labenu-apis.cloudfunctions.net/labefy/playlists"
        axios.get( url, {
            headers: {
                Authorization: "lais-martins-alves"
            }
        })
        .then((res) => {
            this.setState({listas: res.data})
        })
        .catch((erro) => {
            alert("Erro! Tente novamente.")
        })
    }   


  render() {
     const Playlist = this.state.listas.map((user) => {
        return 
            <ContainerPlaylist key={user.id}>
            {user.name}
            <button> "Deletar Playlist" </button>
            <button> "Adicionar MÃºsicas" </button>
            </ContainerPlaylist> 
         
    })
  
    return (
      <div>
      <button onClick={this.props.irCriar}>Criar Nova Playlist</button>
      <hr/>
      {Playlist}
      </div>
    )
  }
}
