import {useState } from 'react'
import Etapa1 from "./Components/Etapa1";
import Etapa2 from "./Components/Etapa2";
import Etapa3 from "./Components/Etapa3";
import Final from "./Components/Final";


function App() {
  const [telaAtual, setTelaAtual] = useState("1")
  // const [escola, setEscola] = useState

  const irParaEtapa1 = () => {
		setTelaAtual("1")
	}

	const irParaEtapa2 = () => {
		setTelaAtual("2")
	}

	const irParaEtapa3 = () => {
		setTelaAtual("3")
	}

  const irParaFinal = () => {
		setTelaAtual("4")
	}

   const renderizaTelaAtual = () => {
		if (telaAtual === "1"){
			return <Etapa1/>
		} else if (telaAtual === "2"){
			return <Etapa2/>
		} else if (telaAtual === "3"){
			return <Etapa3/>
		} else if (telaAtual === "4"){
			return <Final/>
		}
	}




  return (
    <div>
      {renderizaTelaAtual()}
      <button >{telaAtual === "1"? "Próxima Etapa" : "Finalizar"}</button>
    </div>
  );
}

export default App;
