import { useState } from "react"
import Contador from './components/Contador'
import Comentario from './components/Comentario'
import Header from './components/Header'
import Post from './components/Post'

export default function App() {
  const [valorContador, setValorContador] = useState(0);

  const soma = () => {
    setValorContador(valorContador + 1);
  };
  const subtrai = () => {
    setValorContador(valorContador - 1);
  };

  return (
    <>
      <Header
        nomeUsuario={'Usuario'}
        fotoUsuario={'https://picsum.photos/50/50'}
        />
       <Post 
        fotoPost={'https://www.bing.com/images/search?view=detailV2&ccid=pyXlce4B&id=D1DEFBD4FDDB020A877C32708C9F20C2C669615B&thid=OIP.pyXlce4Bt0cyZPU1ftZecgHaHa&mediaurl=https%3a%2f%2fi.pinimg.com%2foriginals%2fa0%2f57%2f9f%2fa0579f26386f6db2519e254412df89b3.jpg&cdnurl=https%3a%2f%2fth.bing.com%2fth%2fid%2fR.a725e571ee01b7473264f5357ed65e72%3frik%3dW2FpxsIgn4xwMg%26pid%3dImgRaw%26r%3d0&exph=1080&expw=1080&q=happy+eleven&simid=608025072939262357&FORM=IRPRST&ck=553932A4F401192A3BDBB90C99E02CD8&selectedIndex=44&ajaxhist=0&ajaxserp=0/200/150'}
      />
  <Contador valorContador={valorContador} soma={soma} subtrai={subtrai} />
  <Comentario/>
    </>
  );
};
