import { useState } from "react"

export default function Post(fotoUsuario, nomeUsuario, fotoPost) {
  // Passo5
  // Crie a lógica de estados funcionais aqui.
  const [curtido, setCurtido] = useState(false);
  const [numeroCurtidas, setNumeroCurtidas] = useState(0);
  const [comentando, setComentando] = useState(false);
  const [comentarios, setComentarios] = useState(0);
  const [numeroComentarios, setNumeroComentarios] = useState([]);
  const [inputValue, setStateImputValue] = useState("");


  // Passo7
  const onClickCurtida = () => {
    // Crie a lógica de onClickCurtida aqui.
    if (curtido) {
      setCurtido({ curtido: !curtido, numeroCurtidas: numeroCurtidas - 1 });
    } else {
      // se false, o estado atualiza curtido para o seu inverso e adiciona 1 like.
      setCurtido({ curtido: !curtido, numeroCurtidas: numeroCurtidas + 1 });
    };
  }


  // Passo7
  const onClickComentario = () => {
    // Crie a lógica de onClickComentario aqui.
    setComentando({ comentando: !this.state.comentando });
  };

  // Passo7
  const onChangeComentario = (event) => {
