import React, { useState } from "react";
import styled from "styled-components";
import { IconeComContador } from "../IconeComContador/IconeComContador";
import iconeCoracaoBranco from "../../img/favorite-white.svg";
import iconeCoracaoPreto from "../../img/favorite.svg";
import iconeComentario from "../../img/comment_icon.svg";
import { SecaoComentario } from "../SecaoComentario/SecaoComentario";

const PostContainer = styled.div`
  border: 1px solid gray;
  width: 300px;
  margin-bottom: 10px;
`;

const PostHeader = styled.div`
  height: 40px;
  display: flex;
  align-items: center;
  padding-left: 10px;
  background-color: white;
`;

const PostFooter = styled.div`
  height: 40px;
  display: flex;
  align-items: center;
  padding: 0 10px;
  justify-content: space-between;
  background-color: white;
`;

const UserPhoto = styled.img`
  height: 30px;
  width: 30px;
  margin-right: 10px;
  border-radius: 50%;
`;

const PostPhoto = styled.img`
  width: 100%;
`;

function Post(props) {
  const [curtido, setCurtido] = useState(false);
  const [numeroCurtidas, setNumeroCurtidas] = useState(0);
  const [comentando, setComentando] = useState(false);
  const [numeroComentarios, setNumeroComentarios] = useState(0);

  const onClickCurtida = () => {
    setCurtido(!curtido);
    console.log("Curtiu!", !curtido);
    setNumeroCurtidas(numeroCurtidas === 0? numeroCurtidas +1 : numeroCurtidas -1)
  };



  const onClickComentario = () => {
    setComentando(!comentando);
  };

  const aoEnviarComentario = () => {
    alert("Mensagem Enviada!")
    setComentando(false);
    setNumeroComentarios(numeroComentarios + 1);
  };

  let iconeCurtida;

  if (curtido) {
    iconeCurtida = iconeCoracaoPreto;
  } else {
    iconeCurtida = iconeCoracaoBranco;
  }

  let componenteComentario;

  if (comentando) {
    componenteComentario = <SecaoComentario aoEnviar={aoEnviarComentario} />;
  }

  return (
    <>


<div>
        <input

          value={valorInputPessoa}
          // Repare na função que é passada aqui no onChange
          onChange={onChangeInputPessoa}
          placeholder={"Nome"}
        />
        <input
          // Diferente do input acima, a variável de estado aqui é outra
          value={valorInputEmail}
          // E a função também é outra
          onChange={onChangeInputEmail}
          placeholder={"Email"}
        />

        <input
          value={valorInputTelefone}
          onChange={onChangeInputTelefone}
          placeholder={"Telefone"}
        />
        <button onClick={adicionaPessoa}>Adicionar</button>
      </div>
      <div>{listaDeComponentes}</div>




    <PostContainer>
      <PostHeader>
        <UserPhoto src={props.fotoUser} alt={"Imagem do usuario"} />
        <p>{props.nomeUser}</p>
      </PostHeader>

      <PostPhoto src={props.fotoPost} alt={"Imagem do post"} />

      <PostFooter>
        <IconeComContador
          icone={iconeCurtida}
          onClickIcone={onClickCurtida}
          valorContador={numeroCurtidas}
        />

             <IconeComContador
          icone={iconeComentario}
          onClickIcone={onClickComentario}
          valorContador={numeroComentarios}
        />

      </PostFooter>
      {componenteComentario}

    </PostContainer>
    </>
  );
}

export default Post;
