import React, { useState } from "react";
import styled from "styled-components";
import Post from "./components/Post/Post";
import logo from './img/insta.png'
// import hugo from './imagens/huguinho.png'
// import ze from './imagens/Zezinho.jpg'
// import luis from './imagens/Luisinho.png'
// import um from './imagens/post1.jpg'


const MainContainer = styled.div`
  display: flex;
  justify-content: center;
  flex-direction: column;
  align-items: center;
  
    
`;

const Img = styled.img`
width: 100px;
`;

const ContPai = styled.div`
width: 300px;
height: 100%;
margin-left: 35%;

`

function Principal() {
  const [posts, setPosts] = useState(
    [
      {
        nomeUsuario: 'Huguinho',
        fotoUsuario: "https://imagensemoldes.com.br/wp-content/uploads/2020/03/Mickey-Louie-Dewey-Huey-Daisy-Tio-Patinhas-PNG-17.png",
        fotoPost: "https://cenasdecinema.com/wp-content/uploads/2020/11/DuckTales-os-cacadores-de-aventura_destaque.jpg"
      },
      {
        nomeUsuario: 'Zezinho',
        fotoUsuario: "https://i.pinimg.com/736x/4d/f1/c5/4df1c510748ff4fd958b72749d12fa84--disney-quiz-nursery-room.jpg",
        fotoPost: "https://th.bing.com/th/id/OIP.3x2c6liChM6MZd9zozRHzgHaFj?w=273&h=205&c=7&r=0&o=5&dpr=1.1&pid=1.7"
      },
      {
        nomeUsuario: 'Luisinho',
        fotoUsuario: "https://i.pinimg.com/474x/b0/db/6f/b0db6fa7b1d63121f7dc4e8d53cd6d62.jpg",
        fotoPost: "https://gifimage.net/wp-content/uploads/2017/10/carregando-gif-animado-9.gif"
      }
    ]
  )

  const [inputNome, setInputNome] = useState("");
  const [fotoInputPerfil, setFotoInputPerfil] = useState("");
  const [fotoInputFoto, setFotoInputFoto] = useState("");

  const AddNovoPost = () => {
    const novoPost = {
      nomeUsuario: inputNome,
      fotoUsuario: fotoInputPerfil,
      fotoPost: fotoInputFoto    
    }
    const postsRecebidos = [...posts, novoPost]
    setPosts(novoPost)
  }

  const onChangeInputNome = (event) => {
     setInputNome(event.target.value);
  };

  const onChangeFotoInputPerfil = (event) => {
    setFotoInputPerfil(event.target.value);
  };

  const onChangeFotoInputFoto = (event) => {
    setFotoInputFoto(event.target.value);
  };

  
  const listaDeComponentes = posts.map((post,index) => {
    return (
      
      <Post key={index}
        nomeUser={post.nomeUsuario} 
        fotoUser={post.fotoUsuario}
        fotoPost={post.fotoPost}
      />
         );
  });
 
  return (
    <ContPai>
      <Img src={logo} />
      <MainContainer>
       {listaDeComponentes}
      </MainContainer>

      <div>
        <input
           value={inputNome}
          
          onChange={onChangeInputPessoa}
          placeholder={"Nome"}
        />
        <input
          // Diferente do input acima, a variável de estado aqui é outra
          value={valorInputEmail}
          // E a função também é outra
          onChange={onChangeInputEmail}
          placeholder={"Email"}
        />

        <input
          value={valorInputTelefone}
          onChange={onChangeInputTelefone}
          placeholder={"Telefone"}
        />
        <button onClick={adicionaPessoa}>Adicionar</button>
      </div>
      <div>{listaDeComponentes}</div>
    
      

    </ContPai>


  );
}

export default Principal;
