import React from "react";
import axios from "axios";
import PokeCard from "./components/PokeCard";

function App() {
   const [pokeList, setPokeList] = useState([]);
  const [pokeName, setPokeName] = useState("");

  // Passo 3c
  // componentDidMount = () => {
  //   axios
  //     .get("https://pokeapi.co/api/v2/pokemon/?limit=151")
  //     .then((res) => {
  //       this.setState({ pokeList: res.data.results });
  //     })
  //     .catch((err) => {
  //       console.log(err);
  //     });
  // };

  useEffect(() => {
    axios
      .get("https://pokeapi.co/api/v2/pokemon/?limit=151")
      .then((res) => {
        setPokeList(res.data.results);
      })
      .catch((err) => {
        console.error("Erro ao buscar Pokemon");
      });
  }, []);


  const changePokeName = (event) => {
    setPokeName(e.target.value);
  };

  // Passo 3e
  const pokeOptions = pokeList.map(pokemon => {
    return (
      <option key={pokemon.name} value={pokemon.name}>
        {pokemon.name}
      </option>
    );
  });

  // Passo 4a
  const pokemon = true && <PokeCard pokeName={pokeName} />;

  return (
    <>
      <header>
        <h1>Exibir Pok√©mon</h1>
      </header>
      <hr />
      <nav>
        <label htmlFor={"select-pokemon"}>Selecione um pokemon: </label>

        <select id={"select-pokemon"}  onChange={changePokeName } >
          <option value={""}>Nenhum</option>

          {pokeOptions}
        </select>
      </nav>
      <main>
        {pokemon}
      </main>
    </>
  );
};

export default App;
