import { useState, useEffect } from "react";
import axios from "axios";
import PokeCard from "./components/PokeCard";
import poke from "./components/imagens/poke.jpg"
import pokeball from "./components/imagens/pokeball.gif"
import styled from "styled-components";


const Header = styled.div`
width: 100%
`

const Main = styled.div`
align-items: center;
text-align: center
`
const Body = styled.div`
display: flex;
justify-content: space-between;
`

function App() {
   const [pokeList, setPokeList] = useState([]);
  const [pokeName, setPokeName] = useState("");


  useEffect(() => {
    axios
      .get("https://pokeapi.co/api/v2/pokemon/?limit=151")
      .then((res) => {
        setPokeList(res.data.results);
      })
      .catch((err) => {
        console.error("Erro ao buscar Pokemon");
      });
  }, []);


  const changePokeName = (e) => {
    setPokeName(e.target.value);
  };

  // Passo 3e
  const pokeOptions = pokeList.map(pokemon => {
    return (
      <option key={pokemon.name} value={pokemon.name}>
        {pokemon.name}
      </option>
    );
  });


  const pokemon = true && <PokeCard pokeName={pokeName} />;

  return (
    <Body>
      <Header>
        <img src = {poke}/>
      </Header>

      <nav>
        <label htmlFor={"select-pokemon"}>Selecione um pokemon: </label>

        <select id={"select-pokemon"}  onChange={changePokeName } >
          <option value={""}>Nenhum</option>

          {pokeOptions}
        </select>
      </nav>
      <Main>
        {pokemon}
      </Main>
      <img src= {pokeball}/>
    </Body>
  );
};

export default App;
