import React, {useState, useEffect} from 'react'
import axios from 'axios'
import TelaMatches from './TelaMatches';

export default function TelaInicial(props) {
    const [perfil, setPerfil] = useState("");
    const[perfis,setPerfis] = useState([])
    
    useEffect(() => {
        axios
          .get("https://us-central1-missao-newton.cloudfunctions.net/astroMatch/:lais-martins-alves/person")
          .then((res) => {
            setPerfil(res.data.results);
          })
          .catch((err) => {
            console.error("Erro ao buscar Perfil");
          });
      }, []);

      const mudaPerfil = (e) => {
        setPerfil(e.target.value);
      };

      const botaoProximo = perfis.map(profile => {
        return (
          <button key={profile.id} value={profile.name}>
            {profile.name}
          </button>
        );
      });

      const profile = true && <TelaMatches perfil={perfil} />;  

  return (
    <div>
        <button onClick={props.vaiParaMatches}>Ir Para Match</button>

        <nav>
        <label htmlFor={"proximo"}><strong> ???</strong></label>
        
        <button id={"proximo perfil"}  onChange={ mudaPerfil } >Proximo </button>
          {/* <button value={""}>Nenhum</button> */}
         
          {/* {botaoProximo} */}
        
      </nav>
      <main>
        {profile}
      </main>
    </div>
  )
}
