import React, {useState, useEffect} from 'react'
import axios from 'axios'
import TelaMatches from './TelaMatches';
import styled from 'styled-components';

const Card = styled.div`
margin-top: 100px;
`

const Img = styled.img`
width: 100px;
`

export default function TelaInicial(props) {
    const [perfil, setPerfil] = useState ({})
  
    const escolherPerfil = () => {
        axios
        .get(`https://us-central1-missao-newton.cloudfunctions.net/astroMatch/:lais-martins-alves/person`)
        .then ( res => {
            setPerfil(res.data.profile)
        console.log(res.data.profile)    
        })
        .catch ( error => alert ('Erro'))
    }
    
    const like = (id) => {
        const body = {
            id: id,
            choice: true
        }
        axios 
        .post(`https://us-central1-missao-newton.cloudfunctions.net/astroMatch/:lais-martins-alves/choose-person`, body)
        .then (res => {
            escolherPerfil()
        })
        .catch ( error => alert ('Erro'))
    } 


    useEffect(() => {
        escolherPerfil ()
           }, [])

    const card =
    <Card>
    <Img src= {perfil.photo}/>
    <p>{perfil.name}, {perfil.age}</p>
    <p>{perfil.bio}</p>
    <button onClick={()=>like(perfil.id)}>Like</button>
    <button onClick={escolherPerfil}>X</button>
    </Card>       


    
  return (
    <div>
        <button onClick={props.vaiParaMatches}>Ir Para Match</button>

       
      <main>
        {escolherPerfil}
    
      </main>

      {card}
    </div>
  )
}
