import React, {useState, useEffect} from 'react'
import axios from 'axios'
import styled from 'styled-components'

const Foto = styled.img`
width: 200px;
height: 300px;
`

const Card = styled.div`
border: 1px solid black;
padding: 20px;
display: flex;
margin: 50px;
width: 600px;
`
const Curtir = styled.div`
display: flex;
flex-direction: column;
width: 250px;
justify-content: space-between
`
const Titulo = styled.p`
font-size: xx-large;
`

const Texto = styled.div`
display: flex;
flex-direction: column;
width: 300px;
margin-left: 50px;
`

export default function TelaInicial(props) {
    const [perfil, setPerfil] = useState ({})
  
    const escolherPerfil = () => {
        axios
        .get(`https://us-central1-missao-newton.cloudfunctions.net/astroMatch/:lais-martins-alves/person`)
        .then ( res => {
            setPerfil(res.data.profile) 
        })
        .catch ( error => alert ('Erro'))
    }
    
    const like = (id) => {
        const body = {
            id: id,
            choice: true
        }
        axios 
        .post(`https://us-central1-missao-newton.cloudfunctions.net/astroMatch/:lais-martins-alves/choose-person`, body )
        .then (res => {
            escolherPerfil()
        })
        .catch ( error => alert ('Erro'))
    } 


    useEffect(() => {
        escolherPerfil ()
           }, [])

    const card = perfil &&
    <Card>
    <Foto src= {perfil.photo} alt="Perfil"/>
    <Texto>
    <Titulo>{perfil.name}, {perfil.age}</Titulo>
    <p>{perfil.bio}</p>
    </Texto>
    </Card>       

  return (
    <div>
     
       

      
      {card}
    

      <Curtir>
    <button onClick={()=>like(perfil.id)}>Like</button>
    <button onClick={escolherPerfil}>X</button>
    </Curtir>
      

    
    
      </div>
  )
}
