import React, {useState, useEffect} from 'react'
import axios from 'axios'
import TelaMatches from './TelaMatches';
import styled from 'styled-components';

const Card = styled.div`
margin-top: 10px;
border: 1px solid black;
width: 300px;
height: 350px;
background-color: #F2BF49;
`

const ContainerCard = styled.div`
margin-top: 10px;
border: 1px solid black;
width: 300px;
height: 350px;
background-color: #F2BF49;
`

const Img = styled.img`
width: 100%;
`

const Titulo = styled.p`
width: 100%;

`
const Bio = styled.p`
width: 100%;
`

const Button = styled.button`
padding: 10px;
border: solid;
border-radius: 100%
`

export default function TelaInicial(props) {
    const [perfil, setPerfil] = useState ({})
  
    const escolherPerfil = () => {
        axios
        .get(`https://us-central1-missao-newton.cloudfunctions.net/astroMatch/:lais-martins-alves/person`)
        .then ( res => {
            setPerfil(res.data.profile)
        console.log(res.data.profile)    
        })
        .catch ( error => alert ('Erro'))
    }
    
    const like = (id) => {
        const body = {
            id: id,
            choice: true
        }
        axios 
        .post(`https://us-central1-missao-newton.cloudfunctions.net/astroMatch/:lais-martins-alves/choose-person`, body)
        .then (res => {
            escolherPerfil()
        })
        .catch ( error => alert ('Erro'))
    } 


    useEffect(() => {
        escolherPerfil ()
           }, [])

    const card =
    <Card>
    <Img src= {perfil.photo}/>
    <Titulo>{perfil.name}, {perfil.age}</Titulo>
    <Bio>{perfil.bio}</Bio>
    <button onClick={()=>like(perfil.id)}>Like</button>
    <button onClick={escolherPerfil}>X</button>
    </Card>       


    
  return (
    <div>
        <Button onClick={props.vaiParaMatches}>Matches</Button>

       
      <main>
        {escolherPerfil}
    
      </main>
    <ContainerCard>
      {card}
      </ContainerCard>
      </div>
  )
}
