import React, {useState, useEffect} from 'react'
import TelaInicial from './components/TelaInicial'
import TelaMatches from './components/TelaMatches'
import styled from 'styled-components'
import astro from './components/imagens/astro.png'

const Header = styled.div`
display: flex;
`
const Img = styled.img`
width: 70px;
height: 50px;
`

const CardPrincipal = styled.div`
border: 1px solid black;
border-radius: 3%;
width: 300px;
height: 350px;
align-items: center
display: flex;
margin-left: 30%

`

export default function App() {
  const [tela, setTela] = useState("inicial");

  const mudarTela = () => {
    switch (tela) {
      case "inicial":
        return <TelaInicial vaiParaMatches={vaiParaMatches} />
      case "matches":
        return <TelaMatches vaiParaInicial={vaiParaInicial} />
      default:
        return <>Erro! Página não encontrada!⚠️</>


    }
  }

  const vaiParaInicial = () => {
    setTela("inicial")
  }

  const vaiParaMatches = () => {
    setTela("matches") 
  }




  return (
    <CardPrincipal>

    <Header>  
     <Img src= {astro}/> 

    {mudarTela()}
    </Header>
       </CardPrincipal>
  )
}
