import React, {useState, useEffect} from 'react'
import {useNavigate} from 'react-router-dom'
import useForm from '../../Hooks/Hooks'
import axios from 'axios'


export default function ViagemEscolhida () {
    const navigate = useNavigate()
    const [detalhes, setDetalhes] = useState({})
    const form = useForm()

    const voltar = () => {
        navigate(-1)
    }

    const detalhesViagem = () => {
        axios.get(`https://us-central1-labenu-apis.cloudfunctions.net/labeX/lais/trip/:id
        /trip/${form.viagem}`, { headers: { 
            auth: 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6Im93T2g5ZWo2bW50akZqNUNRMVB4IiwiZW1haWwiOiJhc3Ryb2RldkBnbWFpbC5jb20uYnIiLCJpYXQiOjE1ODk1NjI5MDh9.aB4dNbTCkToXB7pdzEa-tuMa-QbRQDUd93eva4-cec0' } })
            .then((res) => {
                setDetalhes(res.data.trip)
            })
            .catch((err) => {
            })
    }

    useEffect(() => {
        detalhesViagem()
    }, [])

    const decidindoCandidato = (id, res) => {
        if (res === "approv") {
            const body = {
                approve: true
            }
            axios.put(`https://us-central1-labenu-apis.cloudfunctions.net/labeX/lais/trips/${form.viagem}/candidates/${id}/decide`, body, { headers: { 
                auth: 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6Im93T2g5ZWo2bW50akZqNUNRMVB4IiwiZW1haWwiOiJhc3Ryb2RldkBnbWFpbC5jb20uYnIiLCJpYXQiOjE1ODk1NjI5MDh9.aB4dNbTCkToXB7pdzEa-tuMa-QbRQDUd93eva4-cec0' } })
                .then((res) => {
                    setDetalhes()
                    alert('Candidato Aprovado!')
                })
                .catch((err) => {
                })
        } else if (res === "reprov") {
            const body = {
                approve: false
            }
            axios.put(`https://us-central1-labenu-apis.cloudfunctions.net/labeX/lais/trips/${form.viagem}/candidates/${id}/decide`, body, { headers: { 
                auth: 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6Im93T2g5ZWo2bW50akZqNUNRMVB4IiwiZW1haWwiOiJhc3Ryb2RldkBnbWFpbC5jb20uYnIiLCJpYXQiOjE1ODk1NjI5MDh9.aB4dNbTCkToXB7pdzEa-tuMa-QbRQDUd93eva4-cec0' } })
                .then((res) => {
                    setDetalhes()
                    alert('Candidato Recusado!')
                })
                .catch((err) => {
                })
        }
    }

    const candidato = detalhes.candidates && detalhes.candidates.map((candidato) => {
        return (
            <div key={candidato.id}>
                <div>
                    <p>Candidato</p>
                    <p>Nome: {candidato.nome}</p>
                    <p>Profissão:{candidato.profissao}</p>
                    <p>Idade: {candidato.idade}</p>
                    <p>País:{candidato.pais}</p>
                    <p>Texto: {candidato.texto}</p>
                    <section>
                        <button onClick={() => decidindoCandidato(candidato.id, "approv")}>Aprovar</button>
                        <button onClick={() => decidindoCandidato(candidato.id, "reprov")}>Recusar</button>
                    </section>
                </div>
            </div>
        )
    })

    const aprovado = detalhes.aprovado && detalhes.aprovado.map((candidato) => {
        return (
            <div key={detalhes.id}>
                <li>{candidato.nome}</li>
            </div>
        )
    })


    const cardDetalhes =
    <div>
        <p>Nome: {detalhes.nome}</p>
        <p>Descrição: {detalhes.descricao}</p>
        <p>Planeta: {detalhes.planeta}</p>
        <p>Duração: {detalhes.duracao} dias</p>
        <p>Data: {(detalhes.data).format('DD/MM/YYYY')}</p>
        
    </div>


    

    return (
        <div>
        <button onClick = {voltar}> Voltar </button>
        <p>Viagem escolhida e Candidatos</p>

        <p> {cardDetalhes && candidato} </p>
               
                
    </div>
    )
}
