import React from 'react'
import {useNavigate} from 'react-router-dom'
import useForm from '../../Hooks/Hooks'
import axios from 'axios'

export default function InscreverSe () {
    const navigate = useNavigate()
    const {form, onChange} = useForm({nome:"", idade:"", texto:"", profissao:""})

    const vaiParaHome = () => {
        navigate("/")
    }

    const cadastrando = (e) => {
      e.preventDefault()
          
      axios.post('https://us-central1-labenu-apis.cloudfunctions.net/labeX/:aluno/trips/:id/apply', form)
      .then((res) => {
          console.log(res.data)
          // localStorage.setItem('token', res.data.token)
          navigate('/viagens')
      })
      .catch((error) => {
          console.log(error.response)
          alert('Dados incorretos. Necessário reenvio', error.response)
          navigate('/cadastro')
      })
     }

  return (
    <>
    <p>Inscreva-se</p>
    <button onClick = {vaiParaHome}> Início </button> 

    <form onSubmit={cadastrando}>
      <input
      placeholder = {'Nome'}
      name = 'nome'
      value = {form.nome}
      />
       <input
      placeholder = {'Idade'}
      name = 'idade'
      value = {form.idade}
      />
       <input
      placeholder = {'Texto de Candidatura'}
      name = 'texto'
      value = {form.texto}
      />
       <input
      placeholder = {'Profissão'}
      name = 'profissao'
      value = {form.profissao}
      />
    </form>
    </>
  )

    
}
