import React from 'react'
import { useNavigate } from 'react-router-dom'
import useForm from '../../Hooks/Hooks'
import axios from 'axios'
import styled from 'styled-components'

const Form = styled.form`
display:flex;
flex-direction: column;
width: 400px;
`

export default function CriarViagem() {
    const navigate = useNavigate()
    const { form, onChange } = useForm({
        nome: "", planeta: "", data: "", bio: "", duracao: ""
    })

    const voltar = () => {
        navigate(1)
    }

    const enviar = () => {
        navigate(-1)
    }

  

    const token = localStorage.getItem('token') 

    const criando = (e) => {
        e.preventDefault()
        const body = {
          name: form.nome,
          planet: form.planeta,
          date: form.data,
          description: form.bio,
          durationInDays: form.duracao
        }
        axios.post('https://us-central1-labenu-apis.cloudfunctions.net/labeX/lais/trips', body, { headers: { auth: token } })
         .then((res) => {
            console.log(res.data.trip)
            navigate('/viagens')
          })
          .catch((error) => {
            console.log(error.response)
            alert('Dados incorretos. Necessário reenvio', error.response)
            navigate('/criar')
          })
      }

    return (
    <>
    <button onClick={voltar}> Voltar </button>
    <br/>
            <p>Criar Viagem</p>
    <br/>        
     
            <Form onSubmit={ criando }>
                <input
                    name='nome'
                    value={form.nome}
                    onChange={onChange}
                    placeholder="Nome"
                    required
                />
                <input
                    name='planeta'
                    value={form.planeta}
                    onChange={onChange}
                    placeholder="Planeta"
                    required
                />
                <input
                    name='data'
                    value={form.data}
                    onChange={onChange}
                    placeholder="Data da Viagem"
                    required
                />
                <input
                    name='bio'
                    value={form.bio}
                    onChange={onChange}
                    placeholder="Descrição da Viagem"
                    required
                />
                <input
                    name='duracao'
                    value={form.duracao}
                    onChange={onChange}
                    placeholder="Duração em Dias"
                    required
                />
            </Form> 
    <button onClick={enviar}> Enviar </button>            
    </>
            )

}
