import React from 'react'
import { useNavigate } from 'react-router-dom'
import useForm from '../../Hooks/Hooks'
import axios from 'axios'


export default function CriarViagem() {
    const navigate = useNavigate()
    const { form, onChange } = useForm({
        nome: "", planeta: "", data: "", bio: "", duracao: ""
    })

    const home = () => {
        navigate("/")
    }

    const enviar = () => {
        navigate(-1)
    }

    const criando = (id, e) => {
        e.preventDefault()
        const body = {
          name: form.nome,
          planet: form.planeta,
          date: form.data,
          description: form.bio,
          durationInDays: form.duracao
        }
        axios.post(`https://us-central1-labenu-apis.cloudfunctions.net/labeX/lais/trips/${trip.id}/apply`, body)
          .then((res) => {
            console.log(res.data.trip)
            navigate('/viagens')
          })
          .catch((error) => {
            console.log(error.response)
            alert('Dados incorretos. Necessário reenvio', error.response)
            navigate('/criar')
          })
      }

    return (
    <>
            <p>Criar Viagens</p>
            <button onClick={home}> Início </button>
            <button onClick={enviar}> Enviar </button>

            <form onSubmit={ criando }>
                <input
                    name='nome'
                    value={form.nome}
                    onChange={onChange}
                    placeholder="Nome"
                    required
                />
                <input
                    name='planeta'
                    value={form.planeta}
                    onChange={onChange}
                    placeholder="Planeta"
                    required
                />
                <input
                    name='data'
                    value={form.data}
                    onChange={onChange}
                    placeholder="Data da Viagem"
                    required
                />
                <input
                    name='bio'
                    value={form.bio}
                    onChange={onChange}
                    placeholder="Descrição da Viagem"
                    required
                />
                <input
                    name='duracao'
                    value={form.duracao}
                    onChange={onChange}
                    placeholder="Duração em Dias"
                    required
                />
            </form>        
    </>
            )

}
