import React from 'react'
import {useNavigate} from 'react-router-dom'
import useForm from '../../Hooks/Hooks'
import axios from 'axios'
import styled from 'styled-components'

const Form = styled.form`
display: flex;
flex-direction: column;
width: 500px;

`

export default function InscreverSe () {
    const navigate = useNavigate()
    const {form, onChange} = useForm({nome:"", idade:"", texto:"", profissao:"", pais:""})

    const vaiParaHome = () => {
        navigate("/")
    }


    const cadastrando = (id, e) => {
      e.preventDefault()
      const body = {
        name: form.nome,
        age: form.idade,
        applicationText: form.texto,
        profession: form.profissao,
        country: form.pais
      }
          
      axios.post(`https://us-central1-labenu-apis.cloudfunctions.net/labeX/lais/trips/${id}/apply`, body)
      .then((res) => {
          console.log(res.data)
          // localStorage.setItem('token', res.data.token)
          navigate('/viagens')
      })
      .catch((error) => {
          console.log(error.response)
          alert('Dados incorretos. Necessário reenvio', error.response)
          navigate('/cadastro')
      })
     }

     const selecionar = (cardViagem) => {

     }

  return (
    <>
    <p>Inscreva-se</p>
    <button onClick = {vaiParaHome}> Início </button> 

    <Form onSubmit={cadastrando}>
      <input
      placeholder = {'Nome'}
      name = 'nome'
      value = {form.nome}
      onChange = {onChange}
      />
       <input
      placeholder = {'Idade'}
      name = 'idade'
      value = {form.idade}
      onChange = {onChange}
      />
       <input
      placeholder = {'Texto de Candidatura'}
      name = 'texto'
      value = {form.texto}
      onChange = {onChange}
      />
       <input
      placeholder = {'Profissão'}
      name = 'profissao'
      value = {form.profissao}
      onChange = {onChange}
      />
      <input
      placeholder = {'País'}
      name = 'pais'
      value = {form.pais}
      onChange = {onChange}
      />
      <button>Cadastrar</button>
    </Form>
    </>
  )

    
}
