import React, {useState} from 'react'
import {useNavigate} from 'react-router-dom'
import axios from 'axios'

export default function Login () {
    const navigate = useNavigate ()
    const [form, setForm] = useState({email: "", pasword: ""})

   const voltar = () => {
    navigate (-1)
   } 

   const onChange = (e) => {
    setForm({...form, [e.target.name]: e.target.value})
   }

   const logando = (e) => {
    e.preventDefault()
        
    axios.post('https://us-central1-labenu-apis.cloudfunctions.net/labeX/lais/login')
    .then((res) => {
        console.log(res.data.token)
        localStorage.setItem('token', res.data.token)
        navigate('/adm')
    })
    .catch((error) => {
        alert('E-mail ou Senha incorreto', error.response)
        navigate('/login')
    })
   }

   return (
    <>
    <p>PÃ¡gina de Login</p>
    <button onClick = {voltar}> Voltar </button>

    <br/>
    <br/>
    <form onSubmit={logando}>

    <input
    name = 'email'
    placeholder = 'E-mail'
    type = 'email'
    value = {form.email}
    onChange = {onChange}
    />

    <input
    name = 'password'
    placeholder = 'Senha'
    type = 'password'
    value = {password}
    onChange = {onChange}
    />

    <button > Enviar </button>
    </form>
    </>
   )
}
