import styled from 'styled-components'

export const BigcardContainer = styled.div`
    display: flex;
    flex-direction: column;
    border: 2px black;
    border-style: doble;
    border-radius: 15px;
    padding: 20px 20px;
    margin-bottom: 10px;
    height: 150px;
    width: 400px;
    background-color: white;
    color: black;
    :hover,
button:focus {
    background-color: radial-gradient(black 3px, transparent 4px),
    radial-gradient(black 3px, transparent 4px),
    linear-gradient(#fff 4px, transparent 0),
    linear-gradient(45deg, transparent 74px, transparent 75px, #a4a4a4 75px, #a4a4a4 76px, transparent 77px, transparent 109px),
    linear-gradient(-45deg, transparent 75px, transparent 76px, #a4a4a4 76px, #a4a4a4 77px, transparent 78px, transparent 109px),
    #fff;
    background-size: 109px 109px, 109px 109px,100% 6px, 109px 109px, 109px 109px;
    background-position: 54px 55px, 0px 0px, 0px 0px, 0px 0px, 0px 0px;
    
  color: black;
  border: 2px white;
    border-style: dashed;
   -webkit-transform: scale(1.1);
  transform: scale(1.1);
}
`

export const BigcardImg = styled.img`
    width: 70px;
    margin-right: 10px;
    border-radius: 50%;
`

export const BigcardH4 = styled.h4`
    margin-bottom: 15px;
    margin-left: 25px;
    font-size: large;
`

export const BigcardInicio =styled.div`
    display: flex;
    flex-direction: column;
    justify-items: flex-start;
`
export const Linha1 = styled.div`
display: flex;
`
